mapper.update:
UPDATE EUSER
	SET EUSER_ISDELETE = '1'
	WHERE EUSER_ID = #{EUser_id}

mapper.select:
SELECT * FROM  
(  
	SELECT A.*, ROWNUM RN  
	FROM (SELECT * FROM EUSER T WHERE T.EUSER_ISDELETE = '0' and T.EUser_creater = #{user_id}			
	) A  
	WHERE ROWNUM <= #{length}
)  
WHERE RN > #{start}

