mapper.select:
SELECT * FROM  
(  
	SELECT A.*, ROWNUM RN  
	FROM (SELECT * FROM Project) A  
	WHERE ROWNUM <= #{length}
)  
WHERE RN > #{start}

SELECT * FROM  
(  
	SELECT A.*, ROWNUM RN  
	FROM (
	select * from Project p where p.Project_ID 
	
		NOT
	
	in
		 (select t.Project_ID as Project_ID
  from APPLY t
 WHERE T.CREATER =#{user_id}
 and t.isdelete='0'
union ALL
select a.Project_ID as Project_ID
  from APPLY_UNIT A
 where a.creater = #{user_id}
 and a.isdelete='0')
	
	) A
	WHERE ROWNUM <= #{limit}
)  
WHERE RN > #{start}

SELECT count(*) FROM  
(  
	SELECT A.*, ROWNUM RN  
	FROM (
	select * from Project p where p.Project_ID 
	
		NOT
	
	in
		 (select t.Project_ID as Project_ID
  from APPLY t
 WHERE T.CREATER =#{user_id}
 and t.isdelete='0'
union ALL
select a.Project_ID as Project_ID
  from APPLY_UNIT A
 where a.creater = #{user_id}
 and a.isdelete='0')
	
	) A
	WHERE ROWNUM <= #{limit}
)  
WHERE RN > #{start}
