mapper.select:
SELECT * FROM  
(  
	SELECT A.*, ROWNUM RN  
	FROM (select *
		  from figclass fg
		 where  fg.figclass_isdelete = '0'
		  
		 	 and fg.figclass_id in
		       (select distinct(f.figclass_id)
		          from user_figclass f
		         where f.isdelete = '0'
		           
		         	and f.creater = #{user}
		         )
		   
		
		
			 and fg.figclass_id not in
		       (select distinct(f.figclass_id)
		          from user_figclass f
		         where f.isdelete = '0'
		         
		         	and f.creater = #{user}
		         
		           )
		
	
		and fg.FIGCLASS_CAOGERY = #{caogery}
	
	
		and fg.FIGCLASS_STATUS = #{status}
	
	) A
	WHERE ROWNUM <= #{length}
)  
WHERE RN > #{start}

select count(*)
		  from figclass fg
		 where  fg.figclass_isdelete = '0'
		  
		 	 and fg.figclass_id in
		       (select distinct(f.figclass_id)
		          from user_figclass f
		         where f.isdelete = '0'
		           
		         	and f.creater = #{user}
		         )
		   
		
		
			 and fg.figclass_id not in
		       (select distinct(f.figclass_id)
		          from user_figclass f
		         where f.isdelete = '0'
		         
		         	and f.creater = #{user}
		         
		           )
		
	
		and fg.FIGCLASS_CAOGERY = #{caogery}
	
	
		and fg.FIGCLASS_STATUS = #{status}

SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( select e.freeco_id as project_id, e.freeco_name as project_name, e.freeco_data as project_start, '0' as project_end, '0' as project_caogery, e.freeco_pernum as project_pernum, e.freeco_status as project_status, '0' as project_allnum, e.freeco_day as project_day, e.freeco_datanum as project_datanum, e.FREECO_NUMFILE as project_isbm, e.freeco_creater as project_isdelete from free_costom e where e.freeco_isdelete = '0'
and e.freeco_status = #{status}
and e.freeco_creater = #{user_id}
union select s.scheduled_id as project_id, s.scheduled_name as project_name, s.scheduled_class_start as project_start, s.scheduled_class_end as project_end, '1' as project_caogery, s.scheduled_class_pnumber as project_pernum, s.scheduled_status as project_status, '0' as project_allnum, '0' as project_day, '0' as project_datanum, '1' as project_isbm, y.ssu_userid as project_isdelete from ssuser y left join scheduledshift s on y.ssu_ssid = s.scheduled_id where y.ssu_isdelete = '0' and s.isdelete = '0'
and y.ssu_status = #{status}
and y.ssu_userid = #{user_id}
union
select f.figclass_id as project_id, f.figclass_name as project_name, f.figclass_class_start as project_start, f.figclass_class_end as project_end, '2' as project_caogery, f.figclass_pernum as project_pernum, f.figclass_status as project_status, '0' as project_allnum, '0' as project_day, '0' as project_datanum, '1' as project_isbm, f.FIGCLASS_CREATER as project_isdelete from (select * from figclass fg where fg.figclass_isdelete = '0' and fg.figclass_id in (select distinct(f.figclass_id) from user_figclass f where f.isdelete = '0'
and f.creater = #{user_id}
)) f ) A WHERE ROWNUM <= #{limit} ) WHERE RN > #{start}

SELECT count(*) FROM ( select e.freeco_id as project_id, e.freeco_name as project_name, e.freeco_data as project_start, '0' as project_end, '0' as project_caogery, e.freeco_pernum as project_pernum, e.freeco_status as project_status, '0' as project_allnum, e.freeco_day as project_day, e.freeco_datanum as project_datanum, e.FREECO_NUMFILE as project_isbm, e.freeco_creater as project_isdelete from free_costom e where e.freeco_isdelete = '0'
and e.freeco_status = #{status}
and e.freeco_creater = #{user_id}
union select s.scheduled_id as project_id, s.scheduled_name as project_name, s.scheduled_class_start as project_start, s.scheduled_class_end as project_end, '1' as project_caogery, s.scheduled_class_pnumber as project_pernum, s.scheduled_status as project_status, '0' as project_allnum, '0' as project_day, '0' as project_datanum, '1' as project_isbm, y.ssu_userid as project_isdelete from ssuser y left join scheduledshift s on y.ssu_ssid = s.scheduled_id where y.ssu_isdelete = '0' and s.isdelete = '0'
and y.ssu_status = #{status}
and y.ssu_userid = #{user_id}
union
select f.figclass_id as project_id, f.figclass_name as project_name, f.figclass_class_start as project_start, f.figclass_class_end as project_end, '2' as project_caogery, f.figclass_pernum as project_pernum, f.figclass_status as project_status, '0' as project_allnum, '0' as project_day, '0' as project_datanum, '1' as project_isbm, f.FIGCLASS_CREATER as project_isdelete from (select * from figclass fg where fg.figclass_isdelete = '0' and fg.figclass_id in (select distinct(f.figclass_id) from user_figclass f where f.isdelete = '0'
and f.creater = #{user_id}
)) f ) A
